import React from 'react';
import "./AddUserModal.css";
import 'bootstrap-icons/font/bootstrap-icons.css'; // Import Bootstrap Icons

const AddUserModal = ({ show, closeModal }) => {
  if (!show) return null; // Don't render the modal if it's not visible

  return (
    <div className="modal show" style={{ display: 'center', backgroundColor: 'rgba(0,0,0,0.5)' }}>
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Add User to the System</h5>
            <button  type="button" className="close" onClick={closeModal} >
              <span>&times;</span>
            </button>
          </div>
          <div className="modal-body">
            <form>
              <div className="form-group">
                <label>First Name:</label>
                <input type="text" className="form-control" required />
              </div>
              <div className="form-group">
                <label>Surname:</label>
                <input type="text" className="form-control" required />
              </div>

              <div className="form-group">
                <label>Staff Designation:</label>
                <select className="form-control">
                  <option>Medical Doctor (1) </option>
                  <option>Nurse (2) </option>
                  <option>Administrator (3) </option>
                </select>
              </div>

              <div className="form-group">
                <label>Email Address:</label>
                <input type="email" className="form-control" required />
              </div>

              <div className="form-group">
                <label>Phone Number:</label>
                <input type="number" className="form-control" required />
              </div>

              <div className="form-group">
                <label>Password:</label>
                <input type="password" className="form-control" required />
              </div>

              <div className="form-group">
                <label>Repeat Password:</label>
                <input type="password" className="form-control" required />
              </div>

            </form>
          </div>

          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={closeModal}>Close</button>
            <button type="button" className="btn btn-primary"  onClick={() => alert('Form Submitted!')}>Save</button>
          </div>

        </div>
      </div>
    </div>
  );
};

