import React, { useState } from 'react';
import "./AddUserModal.css";
import 'bootstrap-icons/font/bootstrap-icons.css'; // Import Bootstrap Icons
import { useNavigate } from 'react-router-dom';
import { createUser } from './api';

const AddUserModal = ({ show, closeModal }) => {
  const navigate = useNavigate(); // Use the useNavigate hook
  const [userData, setUserData] = useState({
    username: '',
    password: '',
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    repeatPassword: '',
    roleId: '', // Assuming you have roles managed in your application
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (userData.password !== userData.repeatPassword) {
      setError('Passwords do not match');
      return;
    }
    try {
      const { repeatPassword, ...dataToSubmit } = userData;
      await createUser(dataToSubmit);
      alert('User created successfully!');
      setUserData({ username: '', password: '', firstName: '', lastName: '', email: '', phoneNumber: '', repeatPassword: '', roleId: '' });
      setError('');
    } catch (error) {
      setError('Error creating user');
      console.error(error);
    }
  }; // Don't render the modal if it's not visible

  return (
    <div className="modal show" style={{ display: 'center', backgroundColor: 'rgba(0,0,0,0.5)' }}>
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Add User to the System</h5>
            <button  type="button" className="close" onClick={closeModal} >
              <span>&times;</span>
            </button>
          </div>
          <div className="modal-body">
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>First Name:</label>
                <input type="text" name="firstName" placeholder="First Name" value={userData.firstName} onChange={handleChange} required />
              </div>
              <div className="form-group">
                <label>Surname:</label>
                <input type="text" name="lastName" placeholder="Last Name" value={userData.lastName} onChange={handleChange} required />
              </div>

              <div className="form-group">
                <label>Staff Designation:</label>
                <input type="text" name="roleId" placeholder="Role ID" value={userData.roleId} onChange={handleChange} required />
              </div>

              <div className="form-group">
                <label>Email Address:</label>
                <input type="email" name="email" placeholder="Email" value={userData.email} onChange={handleChange} required />
              </div>

              <div className="form-group">
                <label>Phone Number:</label>
                <input type="text" name="phoneNumber" placeholder="Phone Number" value={userData.phoneNumber} onChange={handleChange} required />
              </div>

              <div className="form-group">
                <label>User Name:</label>
                <input type="text" name="username" placeholder="Username" value={userData.username} onChange={handleChange} required />
              </div>

              <div className="form-group">
                <label>Password:</label>
                <input type="password" name="password" placeholder="Password" value={userData.password} onChange={handleChange} required />
              </div>

              <div className="form-group">
                <label>Repeat Password:</label>
                <input type="password" name="repeatPassword" placeholder="Repeat Password" value={userData.repeatPassword} onChange={handleChange} required />
              </div>

              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={closeModal}>Close</button>
                <button type="button" className="btn btn-primary">Save</button>
              </div>
            </form>
          </div>


        </div>
      </div>
    </div>
  );
};

export default AddUserModal;
