import React, { useState } from "react";
import "./LoginForm.css";
import { useNavigate } from 'react-router-dom';
import backgroundLogo from './images/Carecloud.png';
import signinIcon from './images/signin.png';  // Importing image
import guideinfor from "./images/downlodge.png";


const App = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSignIn = (e) => {
    e.preventDefault();

    if (username || password) {
      setError("Both fields are required!");
    } else {
      setError("");
      // Logic for sign-in can go here
      alert("Signed in successfully");
       // Navigate to the next page (placeholder for now)
       navigate('/sign in'); // Change path if necessary
    }
  };

  return (
    <div className="container">
       
      <div className="login-box">
      <h2>CARE CLOUD HEALTH RECODE SYSTEM</h2>
        <div className="logo">
          <img src={backgroundLogo} alt ='compnay logo'/>        </div>
       
        <form onSubmit={handleSignIn}>
          <div className="input-box">
            <input
              type="text"
              placeholder="User name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="input-box">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          {error && <p className="error">{error}</p>}
          <div className="button-box">
            <button type="submit">Sign In
              <img src={signinIcon} alt ='singn icon'/>
            </button>
            <button type="button" onClick={() => alert("User Guide")}>
              User Guide
              <img src={guideinfor} alt="user guid"/>
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default App;
