import React, { useState } from "react";
import "./App.css";
import signInIcon from "./assets/images/sign-in.png"; // Example image for Sign In button
import userGuideIcon from "./assets/images/user-guide.png"; // Example image for User Guide button

const App = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSignIn = (e) => {
    e.preventDefault();

    if (username.trim() === "" || password.trim() === "") {
      setError("Username and password are required!");
    } else {
      setError("");
      alert("Signed in successfully!");
    }
  };

  return (
    <div className="container">
      <div className="login-box">
        <h2>CARE CLOUD HEALTH RECODE SYSTEM</h2>
        <form onSubmit={handleSignIn}>
          <div className="input-box">
            <input
              type="text"
              placeholder="User name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="input-box">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          {error && <p className="error">{error}</p>}
          <div className="button-box">
            <button type="submit" className="sign-in-btn">
              <img src={signInIcon} alt="Sign In" className="button-icon" />
              Sign In
            </button>
            <button
              type="button"
              className="guide-btn"
              onClick={() => alert("User Guide")}
            >
              <img src={userGuideIcon} alt="User Guide" className="button-icon" />
              User Guide
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default App;
