import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = ({ onSelectRole }) => {
  const [selectedRole, setSelectedRole] = useState('');
  const navigate = useNavigate();

  const handleRoleSelection = (role) => {
    setSelectedRole(role);
    onSelectRole(role);  // Pass the selected role to the App state
    navigate('/sign in');  // After selecting the role, navigate to the main dashboard with the sidebar
  };

  return (
    <div className="dashboard-container">
      <h2>Welcome to the Dashboard</h2>
      <h3>Please select your role:</h3>
      <div>
        <button onClick={() => handleRoleSelection('doctor')}>Doctor</button>
        <button onClick={() => handleRoleSelection('nurse')}>Nurse</button>
        <button onClick={() => handleRoleSelection('developer')}>Developer</button>
      </div>
    </div>
  );
};

export default Dashboard;
