import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Importing Bootstrap CSS

const VerticalMenu = () => {
  // State to toggle the Patients dropdown
  const [isPatientsOpen, setIsPatientsOpen] = useState(false);

  const togglePatientsDropdown = () => {
    setIsPatientsOpen(!isPatientsOpen); // Toggle dropdown
  };

  return (
    <div className="d-flex flex-column p-3" style={{ width: '200px' }}>
      <ul className="nav nav-pills flex-column mb-auto">
        {/* Patients Menu with Dropdown */}
        <li className="nav-item">
          <a href="#!" className="nav-link" onClick={togglePatientsDropdown}>
            Patients
          </a>
          {/* Dropdown for Add and Find Patient */}
          {isPatientsOpen && (
            <ul className="nav flex-column ms-3">
              <li className="nav-item">
                <a href="#!" className="nav-link">Add Patient</a>
              </li>
              <li className="nav-item">
                <a href="#!" className="nav-link">Find Patient</a>
              </li>
            </ul>
          )}
        </li>

        {/* Other Menu Items */}
        <li className="nav-item">
          <a href="#!" className="nav-link">Consultation</a>
        </li>
        <li className="nav-item">
          <a href="#!" className="nav-link">Laboratory</a>
        </li>
        <li className="nav-item">
          <a href="#!" className="nav-link">Account</a>
        </li>
        <li className="nav-item">
          <a href="#!" className="nav-link">Report</a>
        </li>
        <li className="nav-item">
          <a href="#!" className="nav-link">Set up</a>
        </li>
      </ul>
    </div>
  );
};

export default VerticalMenu;
