import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import LoginPage from './LoginPage';  // Ensure the path is correct
import Dashboard from './Dashboard'; 
import VerticalMenu from './VerticalMenu';  // Sidebar component
import './App.css';

const menuConfig = {
  doctor: ["Consultation"],
  nurse: ["Patients", "Laboratory", "Account", "Pharmacy", "Report"],
  developer: [
    "Patients",
    "Consultation",
    "Laboratory",
    "Account",
    "Pharmacy",
    "Report",
    "Set Up",
  ],
};

function App() {
  const [role, setRole] = useState(null);  // Manage role state here
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Manage login state

  // Simulate login success
  const handleLoginSuccess = (username) => {
    setIsLoggedIn(true);
    // For now, let's mock the role based on username
    if (username === 'doctor') {
      setRole('doctor');
    } else if (username === 'nurse') {
      setRole('nurse');
    } else if (username === 'developer') {
      setRole('developer');
    } else {
      setRole('developer');  // Default to developer role
    }
  };

  return (
    <Router>
      <div className="App">
        {isLoggedIn && role ? (
          <VerticalMenu role={role} menuConfig={menuConfig} /> // Pass the role to sidebar component
        ) : null}

        <Routes>
          <Route path="/" element={<LoginPage onLoginSuccess={handleLoginSuccess} />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/sign in" element={<div>Welcome {role}</div>} />  {/* Replace with your main dashboard */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
