import React, { useState, useEffect } from 'react';
import './PatientRegistrationForm.css';
import VerticalMenu from './Components/verticalmenu';
import Header from './Components/Header';

const PatientRegistrationForm = () => {
  const [gender, setGender] = useState('');
  const [guardianGender, setGuardianGender] = useState('');
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    address: '',
    birthday: '',
    phoneNumber: '',
    guardianFirstName: '',
    guardianLastName: '',
    guardianAddress: '',
    guardianBirthday: '',
    guardianPhoneNumber: '',
  });
  const [patients, setPatients] = useState([]);

  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/patients');
      if (response.ok) {
        const data = await response.json();
        setPatients(data);
      } else {
        console.error('Failed to fetch patients');
      }
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/patients', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...formData,
          gender,
          guardianGender,
        }),
      });

      if (response.ok) {
        alert('Patient added successfully!');
        handleClear();
        fetchPatients();
      } else {
        console.error('Failed to add patient');
        alert('Failed to add patient.');
      }
    } catch (error) {
      console.error('Error adding patient:', error);
      alert('Error occurred while adding patient.');
    }
  };

  const handleClear = () => {
    setGender('');
    setGuardianGender('');
    setFormData({
      firstName: '',
      lastName: '',
      address: '',
      birthday: '',
      phoneNumber: '',
      guardianFirstName: '',
      guardianLastName: '',
      guardianAddress: '',
      guardianBirthday: '',
      guardianPhoneNumber: '',
    });
  };

  return (
    <div className="patient-registration">
      <Header />
      <div className="sidebar">
        <VerticalMenu />
      </div>

      <div className="main-contentP">
        <div className="form-containerP">
          <h2>Patient Registration Form</h2>
          <div className="form-section">
            <div className="personal-details">
              <h3>Personal Details</h3>
              <div className="radio-group">
                <label><input type="radio" name="title" /> Mr</label>
                <label><input type="radio" name="title" /> Mrs</label>
                <label><input type="radio" name="title" /> Miss</label>
              </div>

              <div className="input-group">
                <label>First Name</label>
                <input type="text" name="firstName" value={formData.firstName} onChange={(e) => setFormData({ ...formData, firstName: e.target.value })} />
              </div>
              <div className="input-group">
                <label>Last Name</label>
                <input type="text" name="lastName" value={formData.lastName} onChange={(e) => setFormData({ ...formData, lastName: e.target.value })} />
              </div>
              <div className="input-group">
                <label>Address</label>
                <input type="text" name="address" value={formData.address} onChange={(e) => setFormData({ ...formData, address: e.target.value })} />
              </div>

              <div className="input-group">
                <label>Gender</label>
                <select value={gender} onChange={(e) => setGender(e.target.value)}>
                  <option value="" disabled>Select Gender</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                  <option value="Other">Other</option>
                </select>
              </div>

              <div className="input-group">
                <label>Birthday</label>
                <input type="date" name="birthday" value={formData.birthday} onChange={(e) => setFormData({ ...formData, birthday: e.target.value })} />
              </div>
              <div className="input-group">
                <label>Phone Number</label>
                <input type="number" name="phoneNumber" value={formData.phoneNumber} onChange={(e) => setFormData({ ...formData, phoneNumber: e.target.value })} />
              </div>
            </div>

            <div className="guardian-details">
              <h3>Guardian Details</h3>
              <div className="input-group">
                <label>First Name</label>
                <input type="text" name="guardianFirstName" value={formData.guardianFirstName} onChange={(e) => setFormData({ ...formData, guardianFirstName: e.target.value })} />
              </div>
              <div className="input-group">
                <label>Last Name</label>
                <input type="text" name="guardianLastName" value={formData.guardianLastName} onChange={(e) => setFormData({ ...formData, guardianLastName: e.target.value })} />
              </div>
              <div className="input-group">
                <label>Address</label>
                <input type="text" name="guardianAddress" value={formData.guardianAddress} onChange={(e) => setFormData({ ...formData, guardianAddress: e.target.value })} />
              </div>

              <div className="input-group">
                <label>Gender</label>
                <select value={guardianGender} onChange={(e) => setGuardianGender(e.target.value)}>
                  <option value="" disabled>Select Gender</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                  <option value="Other">Other</option>
                </select>
              </div>

              <div className="input-group">
                <label>Birthday</label>
                <input type="date" name="guardianBirthday" value={formData.guardianBirthday} onChange={(e) => setFormData({ ...formData, guardianBirthday: e.target.value })} />
              </div>

              <div className="input-group">
                <label>Phone Number</label>
                <input type="number" name="guardianPhoneNumber" value={formData.guardianPhoneNumber} onChange={(e) => setFormData({ ...formData, guardianPhoneNumber: e.target.value })} />
              </div>
            </div>
          </div>

          <div className="form-buttons">
            <button className="submit-btn" onClick={handleSubmit}>Submit</button>
            <button className="clear-btn" onClick={handleClear}>Clear</button>
          </div>

          <div className="patient-list">
            <h3>All Patients</h3>
            <ul>
              {patients.map((patient) => (
                <li key={patient.id}>{patient.firstName} {patient.lastName}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PatientRegistrationForm;
