import React, { useState } from 'react';

function UserForm() {
  // Define state for the input field
  const [userName, setUserName] = useState('');

  // Function to handle input change
  const handleInputChange = (event) => {
    setUserName(event.target.value);  // Update the state with the input value
  };

  // Function to handle form submission (optional)
  const handleSubmit = (event) => {
    event.preventDefault();  // Prevent the default form submission behavior
    console.log('User Name Submitted: ', userName);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          User Name:
          <input
            type="text"
            value={userName}
            onChange={handleInputChange}
            placeholder="Enter your name"
          />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
      {/* Display the entered user name */}
      <p>Entered Name: {userName}</p>
    </div>
  );
}

export default UserForm;
