import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css'; // Import Bootstrap Icons
import '../Components/VerticalMenu.css'; // Adjust the path as necessary
import patient from '../images/patient.png';
import Consultation from '../images/Consultation (1).png';
import Laboratory from '../images/Laboratory.png';
//import Account from '../images/Account.png';
import Pharmacy from '../images/Pharmacy2.png';
import Report from '../images/Report.png';
import Set_Up from '../images/Set_up.png';





const VerticalMenu = () => {
  // Step 1: Track the currently open menu, including 'patients', 'consultation', and 'laboratory'
  const [activeMenu, setActiveMenu] = useState(null);

  // Step 2: Handle dropdown toggling, ensuring only one is open at a time
  const toggleDropdown = (menu) => {
    setActiveMenu((prevMenu) => (prevMenu === menu ? null : menu)); // Close if clicked again, else open
  };

  return (
    <div className="d-flex flex-column p-3" >
      <ul className="nav nav-pills flex-column mb-auto">

        {/*  Patients Menu with Dropdown */}
        <li className="nav-item">
          <a href="#" className="nav-link" onClick={() => toggleDropdown('patients')}>
            <img src= {patient} alt='' className='nav-img1'></img>
            Patients
            <i className="bi bi-chevron-down ms-2"></i> {/* Down arrow */}
          </a>
          {/* Dropdown for Add and Find Patient */}
          {activeMenu === 'patients' && (
            <ul className="nav flex-column ms-3">
              <li className="nav-item">
                <a href="#" className="nav-link">Add Patient</a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">Find Patient</a>
              </li>
            </ul>
          )}
        </li>

        {/* Consultation Menu  */}
        <li className="nav-item">
          <a href="#" className="nav-link" >
          <img src= {Consultation} alt='' className='nav-img1'></img>
            Consultation
            <i className="bi bi-chevron-down ms-2"></i> {/* Down arrow */}
          </a>
        </li>

        {/* Laboratory Menu */}
        <li className="nav-item">
          <a href="#" className="nav-link" >
          <img src= {Laboratory} alt='' className='nav-img'></img>
          Laboratory
            <i className="bi bi-chevron-down ms-2"></i> {/* Down arrow */}
          </a>
        </li>

        {/* Account Menu with Dropdown */}
        <li className="nav-item">
          <a href="#" className="nav-link" onClick={() => toggleDropdown('Account')}>
          {/*<img src= {Account} alt='' className='nav-img'></img> */}
            Account
            <i className="bi bi-chevron-down ms-2"></i> {/* Down arrow */}
          </a>
          {/* Dropdown for Process Payment*/}
          {activeMenu === 'Account' && (
            <ul className="nav flex-column ms-3">
              <li className="nav-item">
                <a href="#" className="nav-link">Process Payment</a>
              </li>
            </ul>
          )}
        </li>

        {/* Pharmacy Menu with Dropdown */}
        <li className="nav-item">
          <a href="#" className="nav-link" onClick={() => toggleDropdown('Pharmacy')}>
          <img src= {Pharmacy} alt='' className='nav-img'></img>
            Pharmacy
            <i className="bi bi-chevron-down ms-2"></i> {/* Down arrow */}
          </a>
          {/* Dropdown for Give Drugs,Add Drugs and Manage Drugs */}
          {activeMenu === 'Pharmacy' && (
            <ul className="nav flex-column ms-3">
              <li className="nav-item">
                <a href="#" className="nav-link">Give Drugs</a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">Add Drugs</a>
              </li>
              <li className="nav-item">
                <a href="#" className="nav-link">Manage Drugs</a>
              </li>
            </ul>
          )}
        </li>
        {/* Report Menu with Dropdown */}
        <li className="nav-item">
          <a href="#" className="nav-link" onClick={() => toggleDropdown('Report')}>
          <img src= {Report} alt='' className='nav-img'></img>
            Report
            <i className="bi bi-chevron-down ms-2"></i> {/* Down arrow */}
          </a>
          {/* Dropdown for Give Drugs,Add Drugs and Manage Drugs */}
          {activeMenu == 'Report' && (
            <ul className="nav flex-column ms-3">
            <li className="nav-item">
              <a href="#" className="nav-link">Today Sales Report</a>
            </li>
          </ul>

          )}

        </li>


        {/* SetUp Menu with Dropdown */}
        <li className="nav-item">
          <a href="#" className="nav-link" onClick={() => toggleDropdown('Set up')}>
          <img src= {Set_Up} alt='' className='nav-img'></img>
            Set Up
            <i className={`bi ${activeMenu === 'menuName' ? 'bi-chevron-up' : 'bi-chevron-down'} ms-2`}></i>
          </a>
          {/* Dropdown for Give Drugs,Add Drugs and Manage Drugs */}
          {activeMenu == 'Set up' && (
            <ul className="nav flex-column ms-3">
            <li className="nav-item">
              <a href="#" className="nav-link">Add System Users</a>
            </li>
            <li className="nav-item">
              <a href="#" className="nav-link">Manage System Users </a>
            </li>
            <li className="nav-item">
              <a href="#" className="nav-link">Users Log Activity</a>
            </li>
            
          </ul>

          )}

        </li>


      </ul>
    </div>
  );
};

export default VerticalMenu;
