import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css'; // Import the CSS file
import backgroundLogo from './images/Carecloud.png';
import signinIcon from 'C:\Users\Uvindu Pasan\Desktop\firstp\src\images\signin.png';// Importing image

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignIn = (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Both fields are required');
    } else {
      setError('');
      // Navigate to the next page (placeholder for now)
      navigate('/dashboard');
    }
  };

  return (
    <div className="login-container">
      <div className="logo">
        <img src={backgroundLogo} alt="Care Cloud Medical Clinics" />
      </div>

      <h1>CARE CLOUD HEALTH RECODE SYSTEM</h1>

      <form className="login-form" onSubmit={handleSignIn}>
        <div className="input-field">
          <label htmlFor="username">User name</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />

        </div>
        <div className="input-field">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p className="error-message">{error}</p>}
        <div className="button-container">
          <button type="submit" className="signin-btn">Sign In
            <img src={signinIcon} alt ='sign In Icon' className='button-icon'/>
          </button>
          <button type="button" className="user-guide-btn">User Guide</button>
        </div>
      </form>
    </div>
  );
};

export default LoginForm;
