import React, { useState, useEffect } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Container, Paper, Button } from '@mui/material';
import { useParams } from 'react-router-dom';

function Appointments() {
  //const{Id}=useParams();
  const Id= 1;
  const paperStyles = { padding: '50px 20px', width: 600, margin: "20px auto" };
  const [id, setId] = useState('');
  const [ano, setAno] = useState(0);  // We'll use this for auto-incrementing the 'ano'
  const [pname, setPName] = useState('');
  const [dname, setDName] = useState('');
  const [dateAndTime, setDateAndTime] = useState('');
  const [telephoneNo, setTelephoneNo] = useState('');
  const [appointments, setAppointments] = useState([]);
  const [errors, setErrors] = useState({});
  const [parent, setParent] = useState([]);

  // Fetch appointments when the component mounts
  // useEffect(() => {
  //   fetch("http://localhost:8080/api/appointments/getAllAppoinments")
  //     .then(res => res.json())
  //     .then((result) => {
  //       setAppointments(result);

  //       // Determine the max 'ano' and increment it for the next appointment
  //       if (result.length > 0) {
  //         const maxAno = Math.max(...result.map(a => a.ano));
  //         setAno(maxAno + 1);  // Set 'ano' to the next available number
  //       }
  //     });
  // }, []);
  useEffect(() => {
    const fetchParentDetails = async () => {
      try {
        const response = await fetch(`http://localhost:8080/api/patients/${Id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch parent data');
        }
        const userData = await response.json();
        setParent(userData);

      } catch (error) {
        console.error('Error fetching parent data:', error);
      }
    };

    fetchParentDetails();
  }, [Id]);

  console.log("parent",parent)

  const validate = () => {
    const newErrors = {};

    // Patient Name Validation
    if (!pname.trim()) {
      newErrors.pname = 'Patient Name is required.';
    }

    // Doctor Name Validation
    if (!dname.trim()) {
      newErrors.dname = 'Doctor Name is required.';
    }

    // Date and Time Validation
    const selectedDateTime = new Date(dateAndTime);
    if (!dateAndTime || selectedDateTime <= new Date()) {
      newErrors.dateAndTime = 'Date and Time must be in the future.';
    }

    // Telephone No Validation
    const phoneRegex = /^[0-9]{10}$/; // Adjust based on your phone number format
    if (!telephoneNo || !phoneRegex.test(telephoneNo)) {
      newErrors.telephoneNo = 'Telephone No must be a valid 10-digit number.';
    }

    return newErrors;
  };

  const handleAddAppointment = () => {
    const validationErrors = validate();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    const appointment = { ano, pname, dname, dateAndTime, telephoneNo };

    fetch(`http://localhost:8080/api/appointments/Save`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(appointment)
    })
    .then(res => res.json())
    .then((newAppointment) => {
      setAppointments([...appointments, newAppointment]);
      setAno(ano + 1); // Increment 'ano' for the next appointment
      clearForm();
    });
  };

  const handleUpdateAppointment = (id) => {
    const validationErrors = validate();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    const appointment = { ano, pname, dname, dateAndTime, telephoneNo };

    fetch(`http://localhost:8080/api/appointments/updateAppoinment/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(appointment)
    })
    .then(() => {
      setAppointments(appointments.map((a) => (a.id === id ? { ...a, ...appointment } : a)));
      clearForm();
    });
  };

  const handleDeleteAppointment = (id) => {
    fetch(`http://localhost:8080/api/appointments/deleteReview/${id}`, {
      method: "DELETE"
    })
    .then(() => {
      setAppointments(appointments.filter(appointment => appointment.id !== id));
    });
  };

  const clearForm = () => {
    setId('');
    setPName('');
    setDName('');
    setDateAndTime('');
    setTelephoneNo('');
    setErrors({});
  };

  const handleEditClick = (appointment) => {
    setId(appointment.id);
    setAno(appointment.ano);  // If editing, retain the original 'ano'
    setPName(appointment.pname);
    setDName(appointment.dname);
    setDateAndTime(appointment.dateAndTime);
    setTelephoneNo(appointment.telephoneNo);
    setErrors({});
  };

  return (
    <div>
      <Container>
        <Paper elevation={2} style={paperStyles}>
          <h1 style={{
            background: 'linear-gradient(90deg, #3f51b5, #f50057)',
            color: 'white',
            padding: '20px',
            textAlign: 'center',
            borderRadius: '8px',
            boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
          }}>
            ADD OR EDIT APPOINTMENT
          </h1>
          <Box
            component="form"
            sx={{ '& > :not(style)': { m: 1, width: '55ch' } }}
            noValidate
            autoComplete="off"
          >
            {/* Removed Appointment Number field */}
            <TextField
              label="Patient Name (PName)"
              variant="outlined"
              value={pname}
              onChange={(e) => setPName(e.target.value)}
              error={!!errors.pname}
              helperText={errors.pname}
            />
            <TextField
              label="Doctor Name (DName)"
              variant="outlined"
              value={dname}
              onChange={(e) => setDName(e.target.value)}
              error={!!errors.dname}
              helperText={errors.dname}
            />
            <TextField
              label="Date and Time"
              type="datetime-local"
              variant="outlined"
              value={dateAndTime}
              onChange={(e) => setDateAndTime(e.target.value)}
              InputLabelProps={{
                shrink: true,
              }}
              error={!!errors.dateAndTime}
              helperText={errors.dateAndTime}
              fullWidth
            />
            <TextField
              label="Telephone Number"
              variant="outlined"
              value={telephoneNo}
              onChange={(e) => setTelephoneNo(e.target.value)}
              error={!!errors.telephoneNo}
              helperText={errors.telephoneNo}
              fullWidth
            />
            <Button
              variant="contained"
              color="primary"
              onClick={() => id ? handleUpdateAppointment(id) : handleAddAppointment()}
            >
              {id ? "Update" : "Submit"}
            </Button>
          </Box>
        </Paper>

        <h1 style={{
          color: "red",
          textAlign: "center",
          fontSize: "2.5rem",
          fontWeight: "bold",
          textTransform: "uppercase",
          textShadow: "2px 2px 5px rgba(0, 0, 0, 0.3)",
          margin: "20px 0",
          letterSpacing: "1px",
        }}>
          APPOINTMENTS
        </h1>

        {appointments.map((appointment) => (
          <Paper elevation={6} style={{ margin: "10px", padding: "15px", textAlign: "left" }} key={appointment.id}>
            <h3>Appointment Number: {appointment.ano}</h3>
            <p>Patient Name: {appointment.pname}</p>
            <p>Doctor Name: {appointment.dname}</p>
            <p>Date and Time: {appointment.dateAndTime}</p>
            <p>Telephone No: {appointment.telephoneNo}</p>
            <Button variant="contained" color="secondary" onClick={() => handleEditClick(appointment)}>Edit</Button>
            <Button variant="contained" color="error" onClick={() => handleDeleteAppointment(appointment.id)}>Delete</Button>
          </Paper>
        ))}
      </Container>
    </div>
  );
}

export default Appointments;
