import React from "react";
import './Consultation.css';
import VerticalMenu from "./Components/verticalmenu";
import Header from "./Components/Header";


const WaitingList = () => {
  const [patients, setPatients] = useState([]);

  // State to hold search query
  const [search, setSearch] = useState('');

  // Fetch all patients on component mount
  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/patients'); // URL to your backend
        const data = await response.json();
        setPatients(data);
      } catch (error) {
        console.error('Error fetching patients:', error);
      }
    };
    fetchPatients();
  }, []); // Empty array ensures this runs only once after component mounts

  // Filter patients by search query
  const filteredPatients = patients.filter(patient =>
    patient.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="waiting-list-container">
      <Header/>
      <VerticalMenu/>
      <div className="box">
      <div className="header-c">
        <h2 className="hc1">CONSULTATION ROOM WAITING LIST PATIENTS</h2>
      </div>

      
      <div className="button-group">

      </div>
      <div className="search-container">
        <label htmlFor="search">Search:</label>
        <input type="text" id="search" placeholder="Search records..." />
      </div>
      <table className="patients-table">
        <thead>
          <tr>
            <th>No</th>
            <th>Name</th>
            <th>Age</th>
            <th>Gender</th>
            <th>History</th>
            <th>Exam</th>
          
          </tr>
        </thead>
        <tbody>
          <tr>
            <td colSpan="8" className="no-data">
              No data available in table
            </td>
          </tr>
        </tbody>
      </table>
      <div className="pagination">
        <span>Showing 0 to 0 of 0 entries</span>

        <div className="nav-buttons">
          <button className="previous-consaltation">Previous</button>
          <button className="next-consaltation">Next</button>
        </div>
      </div>
      </div>
    </div>
  );
};

export default WaitingList;
