import React, { useState, useEffect } from 'react';
import './PatientRegistrationForm.css';
import VerticalMenu from './Components/verticalmenu';
import Header from './Components/Header';
import { AddUserModal, updateUser, deleteUser, getAllUsers } from './api';

const PatientRegistrationForm = () => {
  const [gender, setGender] = useState('');
  const [guardianGender, setGuardianGender] = useState('');
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    address: '',
    birthday: '',
    phoneNumber: '',
    guardianFirstName: '',
    guardianLastName: '',
    guardianAddress: '',
    guardianBirthday: '',
    guardianPhoneNumber: '',
  });
  const [patients, setPatients] = useState([]);
  const [editingPatientId, setEditingPatientId] = useState(null); // Track editing state

  // Fetch all patients on component load
  useEffect(() => {
    fetchPatients();
  }, []);

  const fetchPatients = async () => {
    try {
      const data = await getAllUsers();
      setPatients(data);
    } catch (error) {
      console.error('Error fetching patients:', error);
    }
  };

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  const handleGuardianGenderChange = (event) => {
    setGuardianGender(event.target.value);
  };

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = async () => {
    try {
      if (editingPatientId) {
        // Update existing patient
        await updateUser(editingPatientId, { ...formData, gender, guardianGender });
        alert('Patient updated successfully!');
      } else {
        // Add new patient
        await AddUserModal({ ...formData, gender, guardianGender });
        alert('Patient added successfully!');
      }
      handleClear();
      fetchPatients();
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('Error occurred while saving patient data.');
    }
  };

  const handleEdit = (patient) => {
    setEditingPatientId(patient.id);
    setGender(patient.gender || '');
    setGuardianGender(patient.guardianGender || '');
    setFormData({
      firstName: patient.firstName,
      lastName: patient.lastName,
      address: patient.address,
      birthday: patient.birthday,
      phoneNumber: patient.phoneNumber,
      guardianFirstName: patient.guardianFirstName,
      guardianLastName: patient.guardianLastName,
      guardianAddress: patient.guardianAddress,
      guardianBirthday: patient.guardianBirthday,
      guardianPhoneNumber: patient.guardianPhoneNumber,
    });
  };

  const handleDelete = async (id) => {
    try {
      await deleteUser(id);
      alert('Patient deleted successfully!');
      fetchPatients();
    } catch (error) {
      console.error('Error deleting patient:', error);
      alert('Error occurred while deleting patient.');
    }
  };

  const handleClear = () => {
    setEditingPatientId(null);
    setGender('');
    setGuardianGender('');
    setFormData({
      firstName: '',
      lastName: '',
      address: '',
      birthday: '',
      phoneNumber: '',
      guardianFirstName: '',
      guardianLastName: '',
      guardianAddress: '',
      guardianBirthday: '',
      guardianPhoneNumber: '',
    });
  };

  return (
    <div className="patient-registration">
      <Header />
      <div className="sidebar">
        <VerticalMenu />
      </div>

      <div className="main-contentP">
        <div className="form-containerP">
          <h2>{editingPatientId ? 'Edit Patient' : 'Patient Registration Form'}</h2>

          {/* Form for adding/editing patient */}
          <div className="form-section">
            {/* Personal Details */}
            <div className="personal-details">
              <h3>Personal Details</h3>
              <div className="input-group">
                <label>First Name</label>
                <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} />
              </div>
              <div className="input-group">
                <label>Last Name</label>
                <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} />
              </div>
              <div className="input-group">
                <label>Gender</label>
                <select value={gender} onChange={handleGenderChange}>
                  <option value="" disabled>Select Gender</option>
                  <option value="Male">Male</option>
                  <option value="Female">Female</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="input-group">
                <label>Address</label>
                <input type="text" name="address" value={formData.address} onChange={handleChange} />
              </div>
              {/* Additional personal fields */}
            </div>

            {/* Submit and Clear Buttons */}
            <div className="form-buttons">
              <button className="submit-btn" onClick={handleSubmit}>
                {editingPatientId ? 'Update' : 'Submit'}
              </button>
              <button className="clear-btn" onClick={handleClear}>Clear</button>
            </div>
          </div>

          {/* List of patients */}
          <div className="patient-list">
            <h3>All Patients</h3>
            <ul>
              {patients.map((patient) => (
                <li key={patient.id}>
                  {patient.firstName} {patient.lastName}
                  <button onClick={() => handleEdit(patient)}>Edit</button>
                  <button onClick={() => handleDelete(patient.id)}>Delete</button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PatientRegistrationForm;
