import React, { useState } from 'react';  
import { useNavigate } from 'react-router-dom';
import './Logingpage.css';  // Ensure you have this CSS file linked
import signinIcon from './images/signin.png';  
import guideinfor from "./images/downlodge.png";

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignIn = (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Both fields are required');
    } else {
      setError('');
      navigate('/sign-in'); // Change the path if needed
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSignIn}>
        <div className="form-row">
          <label htmlFor="username" className="form-label">User name</label>
          <input
            type="text"
            id="username"
            className="input-field"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-row">
          <label htmlFor="password" className="form-label">Password</label>
          <input
            type="password"
            id="password"
            className="input-field"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        {error && <p className="error-message">{error}</p>}

        <div className="button-container">
          <button className="sign-in-button" type="submit">
            <img src={signinIcon} alt="Sign In Icon" className="button-icon" />
            Sign In
          </button>
          <button className="user-guide-button" type="button">
            <img src={guideinfor} alt="User Guide Icon" className="button-icon" />
            User Guide
          </button>
        </div>
      </form>
    </div>
  );
};

export default LoginPage;
