import React, { useState } from 'react';

const PatientRegistrationForm = () => {
  const [title, setTitle] = useState(''); // State for radio buttons
  const [gender, setGender] = useState('');
  const [guardianGender, setGuardianGender] = useState('');
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    address: '',
    birthday: '',
    phoneNumber: '',
    guardianFirstName: '',
    guardianLastName: '',
    guardianAddress: '',
    guardianBirthday: '',
    guardianPhoneNumber: '',
  });

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  const handleGuardianGenderChange = (event) => {
    setGuardianGender(event.target.value);
  };

  const handleChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleClear = () => {
    setTitle('');
    setGender('');
    setGuardianGender('');
    setFormData({
      firstName: '',
      lastName: '',
      address: '',
      birthday: '',
      phoneNumber: '',
      guardianFirstName: '',
      guardianLastName: '',
      guardianAddress: '',
      guardianBirthday: '',
      guardianPhoneNumber: '',
    });
  };

  return (
    <div className="patient-registration">
      <div className="form-container">
        <h2>Patient Registration Form</h2>

        <div className="personal-details">
          <h3>Personal Details</h3>
          <div className="radio-group">
            <label>
              <input
                type="radio"
                name="title"
                value="Mr"
                checked={title === 'Mr'}
                onChange={handleTitleChange}
              /> Mr
            </label>
            <label>
              <input
                type="radio"
                name="title"
                value="Mrs"
                checked={title === 'Mrs'}
                onChange={handleTitleChange}
              /> Mrs
            </label>
            <label>
              <input
                type="radio"
                name="title"
                value="Miss"
                checked={title === 'Miss'}
                onChange={handleTitleChange}
              /> Miss
            </label>
          </div>

          {/* Other input fields */}
        </div>

        {/* Submit and Clear Buttons */}
        <div className="form-buttons">
          <button className="submit-btn" onClick={() => alert('Form Submitted!')}>Submit</button>
          <button className="clear-btn" onClick={handleClear}>Clear</button>
        </div>
      </div>
    </div>
  );
};

export default PatientRegistrationForm;
