import React, { useState } from 'react';  // Add useState here
import { useNavigate } from 'react-router-dom';
import './App.css'; // Your CSS
//import backgroundLogo from './images/Carecloud.png';
import signinIcon from './images/signin.png';  // Importing the image for the Sign In button
import guideinfor from './images/downlodge.png';  // Another image

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignIn = (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Both fields are required');
    } else {
      setError('');
      navigate('/dashboard'); // Change path as necessary
    }
  };

  return (
    <div className="login-container">
      <div className="overlay">
        <div className="logo">
          <img src={backgroundLogo} alt="Company Logo" />
        </div>
        <div className="logo-container">
          <h1>CARE CLOUD <br/>HEALTH RECODE SYSTEM</h1>
        </div>
        <div className="login-box">
          <form className="login-form" onSubmit={handleSignIn}>
            <label htmlFor="username">User name</label>
            <input 
              type="text" 
              id="username" 
              value={username}
              onChange={(e) => setUsername(e.target.value)} 
              required 
            />

            <label htmlFor="password">Password</label>
            <input 
              type="password" 
              id="password" 
              value={password}
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />

            {error && <p className='error-message'>{error}</p>}

            <button className="sign-in-button" type="submit">
              {/* Image inside button */}
              <img src={signinIcon} alt="Sign In Icon" className="button-icon" />
              Sign In
            </button>

            <button className="user-guide-button" type="button">
              <img src={guideinfor} alt="Guide Information" className="button-icon" />
              User Guide
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
