import React from 'react';
import { useNavigate, } from 'react-router-dom';
import './App.css'; // Your CSS
import backgroundLogo from './images/Carecloud.png';
import signinIcon from './images/signin.png';  // Importing image
import guideinfor from "./images/downlodge.png";

const LoginPage = () => {
  const navigate = useNavigate();

  const handleSignIn = () => {
    // Here you can add your authentication logic if needed
    navigate('/sign in'); // Navigate to the registration form
  };

  return (
    <div className="login-container">
      <div className="overlay">
      <div class="logo">
      <img src={backgroundLogo} alt="Company Logo" />
      </div>
      <div className="logo-container">
            <h1>CARE CLOUD <br/>HEALTH RECODE SYSTEM</h1>
          </div>
        <div className="login-box">
          <div className="login-form">
            <input type="text" placeholder="User name" className="input-field" />
            <input type="password" placeholder="Password" className="input-field" />

            <div className="button-container">
              <button className="sign-in-button" onClick={handleSignIn}>
               <img src={signinIcon} alt="Sign In Icon" className="button-icon" />
                 Sign In
              </button>

              <button className="user-guide-button">
                <img src={userguideinfo} alt ="Guide infromaton" className="button-icon"/>
                 User Guid
              </button>
              
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
