import React, { useState } from 'react'; 
import { useNavigate } from 'react-router-dom';
import './Logingpage.css';
import backgroundLogo from './images/Carecloud.png';
import signinIcon from './images/signin.png';  
import guideinfor from './images/downlodge.png';

const LoginPage = ({ onLoginSuccess }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignIn = (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Both fields are required');
    } else {
      setError('');
      // Simulating a successful login
      onLoginSuccess(username);  // Assuming username contains the user’s role or an identifier
      navigate('/dashboard');
    }
  };

  return (
    <div className="login-container">
      <div className="overlay">
        <div className="logo">
          <img src={backgroundLogo} alt="Company Logo" />
        </div>
        <div className="logo-container">
          <h1>CARE CLOUD <br/>HEALTH RECORD SYSTEM</h1>
        </div>
        <div className="login-box">
          <form className="login-form" onSubmit={handleSignIn}>
            <input 
              type="text" 
              id="username" 
              className="input-field"
              value={username}
              onChange={(e) => setUsername(e.target.value)} 
              required 
              placeholder="User Name" 
            />
            <input 
              type="password" 
              id="password" 
              className="input-field"
              value={password}
              onChange={(e) => setPassword(e.target.value)} 
              required 
              placeholder="Password" 
            />

            {error && <p className='error-message'>{error}</p>}

            <div className="button-container">
              <button className="sign-in-button" type="submit">
                <img src={signinIcon} alt="Sign In Icon" className="button-icon" />
                Sign In
              </button>

              <button className="user-guide-button" type="button">
                <img src={guideinfor} alt="Guide Information" className="button-icon" />
                User Guide
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
