import React from 'react';
import { useNavigate, } from 'react-router-dom';
import './App.css'; // Your CSS
import backgroundLogo from './images/Carecloud.png';
import signinIcon from './images/signin.png';  // Importing image
import guideinfor from "./images/downlodge.png";
import  { useState } from 'react';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();


  const handleSignIn = (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError('Both fields are required');
    } else {
      setError('');
      // Navigate to the next page (placeholder for now)
      navigate('/sign in');
    };
  };

  return (
    <div className="login-container">
      <div className="overlay">
      <div class="logo">
      <img src={backgroundLogo} alt="Company Logo" />
      </div>
      <div className="logo-container">
            <h1>CARE CLOUD <br/>HEALTH RECODE SYSTEM</h1>
          </div>
        <div className="login-box">
          <div className="login-form" onSubmit={handleSignIn}>

          <label htmlFor="username">User name</label>
            <input type="text" 
            id="User name"
            value={username}
            onChange={(e) => setUsername(e.target.value)} 
            required/>

            <input type="password"
             id="Password" 
             value={password}
             onChange={(e) => setPassword(e.target.value)}
             required />
            <div>
            {error && <p className='error-message'>{error} </p>}
        
              <button className="sign-in-button" onClick={handleSignIn}>
               <img src={signinIcon} alt="Sign In Icon" className="button-icon" />
                 Sign In
              </button>

              <button className="user-guide-button">
                <img src={guideinfor} alt ="Guide infromaton" className="button-icon"/>
                 User Guid
              </button>
              </div>

            </div>
          </div>
        </div>
      </div>
  );
};

export default LoginPage;
